import React from "react";
import classes from "./MealsSummary.module.css";

//class that contains just a summary or a description of our webpage
const MealsSummary = () => {
    return <section className={classes.summary}>
        <h2>Delicious Food, Delivered With Love To You!</h2>
        <p>Choose your favorite meal from our broad selection of available meals and enjoy a delicious 
            lunch or dinner at home!</p>
        <p>All our meals are cooked with high-quality ingredients, just-in-time and of course by experienced chefs!</p>
    </section>
}

export default MealsSummary;
