import { useReducer } from "react";
import CartContext from "./cart-context";

const defaultCartState = {
    items: [],
    totalAmount: 0
};

const cartReducer = (state, action) => {
    if (action.type === "ADD_CART_ITEM"){
        const updatedTotalAmount = state.totalAmount + action.item.price * action.item.amount;

        const existingCartItemIndex = state.items.findIndex(item => item.id === action.item.id);
        const existingCartItem = state.items[existingCartItemIndex];

        let updatedItems;

        if(existingCartItem){
            const updatedItem = {
                ...existingCartItem,
                amount: existingCartItem.amount + action.item.amount
            }

            updatedItems = [...state.items];
            updatedItems[existingCartItemIndex] = updatedItem;
        }else{
            updatedItems= updatedItems = state.items.concat(action.item);
        }

        return {
            items: updatedItems,
            totalAmount: updatedTotalAmount
        }
    }
    if (action.type === "REMOVE_CART_ITEM"){
        const existingCartItemIndex = state.items.findIndex(item => item.id === action.id);
        const existingCartItem = state.items[existingCartItemIndex];
        const updatedTotalAmount = state.totalAmount - existingCartItem.price;

        let updatedItems;
        if(existingCartItem.amount ===1){
            updatedItems= state.items.filter(item => item.id !== action.id)
        }else{

        }
    }
    return defaultCartState;
};

//class that manages the cart data and provides the content of it to all the components
const CartProvider = (props) => {
    const [cartSate, dispatchCartAction] = useReducer(cartReducer, defaultCartState);

    const addItemToCartHandler = item => {
        dispatchCartAction({type: "ADD_CART_ITEM", item: item});
    };

    const removeItemFromCartHandler = id => {
        dispatchCartAction({type: "REMOVE_CART_ITEM", id: id});
    };

    const cartContext = {
        items: cartSate.items, 
        totalAmount: cartSate.totalAmount,
        addItem: addItemToCartHandler,
        removeItem: removeItemFromCartHandler,

    };


    return <CartContext.Provider value={cartContext}>
        {props.children}
    </CartContext.Provider>
}

export default CartProvider;
